a:bk.name,
b:bk.author,
c:bk.categoryName,
d:bk.wordNum,
e:bk.chapterName,
f:bk.intro,
g:bk.picture,
h:"http://statics.rungean.com/static/book/zip/"+parseInt((1999+bid)/2000)+"/"+bid+".zip,{'type':'zip'}"
};
})
</js>

